import React, { Component } from "react";
import Listadenotas from "./components/listadenotas";
import Formulario from "./components/formulario/formulario";
import "./assets/index.css";
class App extends Component {
  constructor() {
    super();
    this.notas = [];
    this.state = {}; //pede pra renderizar, já que não é cert usar o this.render
  }

  criarNota(titulo, texto) {
    const novaNota = { titulo, texto };
    this.notas.push(novaNota); //associa ao array
    this.setState({
      notas: this.notas 
    });//atualiza o estado interno desse componente
  }
  //criando um objeto que contém titulo e texto

  render() {
    return (
      <section className="conteudo">
        <Formulario criarNota={this.criarNota.bind(this)} />
        <Listadenotas notas={this.notas} />
      </section>
    );
  }
}

export default App;
